import React from "react";
import { BrowserRouter as Router, Routes, Route, NavLink } from "react-router-dom";
import FileUpload from "./components/FileUpload/FileUpload";
import History from "./components/History/History";

const App: React.FC = () => {
  return (
    <Router>
      <div className="min-h-screen flex flex-col items-center bg-gray-100">
        {/* Header */}
        <header className="w-full bg-gradient-to-r from-blue-500 to-purple-500 py-4 text-white text-center rounded-b-lg shadow-md">
          <h1 className="text-3xl font-bold">Transcripto</h1>
        </header>

        {/* Navigation */}
        <nav className="flex justify-center space-x-4 mt-6">
          <NavLink
            to="/"
            className={({ isActive }) =>
              `px-4 py-2 rounded-lg ${
                isActive
                  ? "bg-blue-600 text-white"
                  : "bg-gray-300 text-gray-700 hover:bg-gray-400"
              }`
            }
          >
            Real-Time Transcription
          </NavLink>
          <NavLink
            to="/history"
            className={({ isActive }) =>
              `px-4 py-2 rounded-lg ${
                isActive
                  ? "bg-blue-600 text-white"
                  : "bg-gray-300 text-gray-700 hover:bg-gray-400"
              }`
            }
          >
            History
          </NavLink>
        </nav>

        {/* Main Content */}
        <main className="flex-grow w-full flex justify-center mt-8">
          <Routes>
            <Route path="/" element={<FileUpload />} />
            <Route path="/history" element={<History />} />
          </Routes>
        </main>

        {/* Footer */}
        <footer className="w-full bg-gray-200 text-center py-4 mt-8 text-sm text-gray-500">
          &copy; 2024 Transcripto. All rights reserved.
        </footer>
      </div>
    </Router>
  );
};

export default App;
