import React, { useState } from "react";
import axios from "axios";
import FileUploader from "../FileUploader/FileUploader";
import ResultDisplay from "../ResultDisplay/ResultDisplay";
import "./FileUpload.css"; // Ensure this file exists in the same folder

const FileUpload: React.FC = () => {
  const [file, setFile] = useState<File | null>(null);
  const [transcription, setTranscription] = useState<string>("");
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string>("");

  const handleFileUpload = (uploadedFile: File) => {
    setFile(uploadedFile);
    setError("");
  };

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setTranscription("");

    if (!file) {
      setError("Please select a file before submitting.");
      setLoading(false);
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post("http://127.0.0.1:5000/transcribe", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setTranscription(response.data.text);
    } catch (err) {
      setError("Something went wrong. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="file-upload">
      <h1 className="text-xl font-semibold mb-4">Transcripto</h1>
      <form onSubmit={handleSubmit} className="flex flex-col items-center gap-4">
        <FileUploader onUpload={handleFileUpload} />
        <button
          type="submit"
          disabled={loading}
          className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600"
        >
          {loading ? "Transcribing..." : "Upload & Transcribe"}
        </button>
      </form>
      {error && <div className="text-red-500 mt-4">{error}</div>}
      {transcription && <ResultDisplay transcription={transcription} />}
    </div>
  );
};

export default FileUpload;
