  };

  const handleURLSubmit = async (url: string) => {
    // Handle URL transcription logic
    console.log(url);
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 text-gray-800">
      <h1 className="text-4xl font-bold mb-8">Transcripto</h1>

      <div className="w-full max-w-2xl bg-white shadow-lg rounded-lg p-8">
        <h2 className="text-2xl font-semibold mb-6">Real-Time Transcription</h2>
        <div className="tabs flex justify-center gap-4 mb-8">
          <button className="tab bg-blue-600 text-white px-6 py-2 rounded-lg">
            Live Transcription
          </button>
          <button className="tab bg-gray-200 text-gray-600 px-6 py-2 rounded-lg">
            History
          </button>
        </div>

        <FileUploader onUpload={handleFileUpload} />
        <div className="my-6 text-center font-medium text-gray-500">OR</div>
        <URLInput onSubmit={handleURLSubmit} />

        {transcription && <ResultDisplay transcription={transcription} />}
      </div>
    </div>
  );
};

export default App;
