import React, { useState } from "react";
import axios from "axios";
import { Button } from "@/components/ui/button"; // Import ShadCN's Button component
import "./FileUpload.css"; // Ensure this file exists in the same folder

const FileUpload: React.FC = () => {
  const [file, setFile] = useState<File | null>(null);
  const [transcription, setTranscription] = useState<string>("");
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string>("");

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFile(e.target.files?.[0] || null);
  };

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    if (!file) {
      setError("Please select a file before submitting.");
      setLoading(false);
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post("http://127.0.0.1:5000/transcribe", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setTranscription(response.data.text);
    } catch (err) {
      setError("Something went wrong. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="file-upload">
      <h1>Transcripto</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="file"
          onChange={handleFileChange}
          className="file-input"
        />
        <Button type="submit" disabled={loading} variant="default">
          {loading ? "Transcribing..." : "Upload & Transcribe"}
        </Button>
      </form>
      {error && <div className="error-message">{error}</div>}
      {transcription && <p className="transcription-output">Transcription: {transcription}</p>}
    </div>
  );
};

export default FileUpload;
