import React, { useState } from "react";
import axios from "axios";
import FileUploader from "../FileUploader/FileUploader";
import ResultDisplay from "../ResultDisplay/ResultDisplay";
import "./FileUpload.css"; // Ensure this file exists

const FileUpload: React.FC = () => {
  const [file, setFile] = useState<File | null>(null);
  const [transcription, setTranscription] = useState<string>("");
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string>("");

  const handleFileUpload = (uploadedFile: File) => {
    setFile(uploadedFile);
    setError("");
  };

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setTranscription("");

    if (!file) {
      setError("Please select a file before submitting.");
      setLoading(false);
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post("http://127.0.0.1:5000/transcribe", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setTranscription(response.data.text);
    } catch (err) {
      setError("Something went wrong. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="bg-white shadow-lg rounded-lg p-8 max-w-lg w-full">
        <h1 className="text-2xl font-bold mb-6 text-center">Welcome to Transcripto</h1>
        <form onSubmit={handleSubmit} className="flex flex-col gap-4">
          <FileUploader onUpload={handleFileUpload} />
          <button
            type="submit"
            disabled={loading}
            className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {loading ? "Transcribing..." : "Upload & Transcribe"}
          </button>
        </form>
        {error && <div className="text-red-500 mt-4 text-center">{error}</div>}
        {transcription && <ResultDisplay transcription={transcription} />}
      </div>
    </div>
  );
};

export default FileUpload;
